import React, { useState } from 'react'
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import './SearchBox.css'
const SearchBox = () => {
    let[city,setCity]=useState("");

    const API_URL="https://api.openweathermap.org/data/2.5/weather";

   const API_KEY="b494e0c96098da07faa79dca9534db25";

    let getWeatherInfo =async(city)=>{
        let response= await fetch(`${API_URL}?=${city}&appid=${API_KEY}&units=metric`);
        let jsonResponse = await response.json();
       
        let result={
            temp:jsonResponse.main.temp,
            tempMin:jsonResponse.main.temp_min,
            tempMax: jsonResponse.main.temp_max,
            humidity: jsonResponse.main.humidity,
            feelsLike:jsonResponse.main.feels_like,
            weather: jsonResponse.main.weather
        };
        console.log(result)
    };
   
   
  
    let handleChange =(evt)=>{
        setCity(evt.target.value) ;
    };

    let handleSubmit =(evt)=>{
        evt.preventDefault();
        console.log(city);
        setCity("");
        getWeatherInfo();
    };
    return (
        <><div className='search'> 
            
            <h1>search for the weather</h1>
            <form onSubmit={handleSubmit}>

                <TextField id="City" label="City Name" variant="outlined"  required value={city}
                onChange={handleChange}
                /><br /><br />
                <Button variant="contained" type='submit'> Search</Button>
            </form>
        </div>

        </>
    )
}

export default SearchBox
